import { PinMutationReponse } from "@/hooks/pinata";
import axios, { AxiosHeaders } from "axios";

const pinataGateway = "apricot-deep-marmoset-708.mypinata.cloud";
const pinataJWT =
  "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..KCbSOyFUxZa3x6Kyq_pu5t3ZwHkOQLu6nchXSbVMXJU";

const authorized_header = {
  Authorization: `Bearer ${pinataJWT}`,
};

export const fetchJsonByCid = (cid?: string) => {
  return axios
    .get(`http://${pinataGateway}/ipfs/${cid}`)
    .then((res) => res.data);
};

export const pinJsonToIpfs = (
  json: Record<string, string>
): Promise<PinMutationReponse> => {
  console.log(json);
  return axios
    .post(
      "https://api.pinata.cloud/pinning/pinJSONToIPFS",
      { pinataContent: json },
      { headers: authorized_header }
    )
    .then((res) => res.data);
};
